import { ImageService } from './../../../shared/components/image-selector/image.service';
import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Observable, Subscription } from 'rxjs';
import { BlogPostService } from '../services/blog-post.service';
import { BlogPost } from '../models/blog-post.model';
import { CategoryService } from '../../category/services/category.service';
import { Category } from '../../category/models/category.model';
import { EditBlogPost } from '../models/edit-blog-post-model';

@Component({
  selector: 'app-edit-blogpost',
  templateUrl: './edit-blogpost.component.html',
  styleUrls: ['./edit-blogpost.component.css'],
})
export class EditBlogpostComponent implements OnInit, OnDestroy {
  /* Variables */
  id: string | null = null;
  model?: BlogPost;
  categories$?: Observable<Category[]>;
  selectedCategories?: string[];
  isImageSelectorVisible: boolean = false;

  /* Subscriptions */
  routeSubscription?: Subscription;
  editBlogPostSubscription?: Subscription;
  getBlogPostSubscription?: Subscription;
  deleteBlogPostSubscription?: Subscription;
  imageSelectSubscription?: Subscription;

  constructor(
    private route: ActivatedRoute,
    private blogPostService: BlogPostService,
    private categoryService: CategoryService,
    private router: Router,
    private imageService: ImageService
  ) {}

  ngOnInit(): void {
    this.categories$ = this.categoryService.getAllCategories();

    this.routeSubscription = this.route.paramMap.subscribe({
      next: (params) => {
        this.id = params.get('id');

        //Get BlogPost from API
        if (this.id) {
          this.getBlogPostSubscription = this.blogPostService
            .getBlogPostById(this.id)
            .subscribe({
              next: (res) => {
                this.model = res;
                this.selectedCategories = res.categories.map((x) => x.id);
              },
            });
        }
        this. imageSelectSubscription = this.imageService.onSelectImage().subscribe({
          next: (res) => {
            if (this.model) {
              this.model.featuredImageUrl = res.url;
              this.isImageSelectorVisible = false;
            }
          },
        });
      },
    });
  }

  onFormSubmit(): void {
    // Convert this model to Request Object
    if (this.model && this.id) {
      var editBlogPost: EditBlogPost = {
        author: this.model.author,
        content: this.model.content,
        shortDescription: this.model.shortDescription,
        featuredImageUrl: this.model.featuredImageUrl,
        isVisible: this.model.isVisible,
        publishedDate: this.model.publishedDate,
        title: this.model.title,
        urlHandle: this.model.urlHandle,
        categories: this.selectedCategories ?? [],
      };

      this.editBlogPostSubscription = this.blogPostService
        .editBlogPost(this.id, editBlogPost)
        .subscribe({
          next: (res) => {
            this.router.navigateByUrl('/admin/blogposts');
          },
        });
    }
  }

  onDelete(): void {
    if (this.id) {
      this.deleteBlogPostSubscription = this.blogPostService
        .deleteBlogPost(this.id)
        .subscribe({
          next: (res) => {
            this.router.navigateByUrl('/admin/blogposts');
          },
        });
    }
  }

  openImageSelector(): void {
    this.isImageSelectorVisible = true;
  }

  closeImageSelector(): void {
    this.isImageSelectorVisible = false;
  }

  ngOnDestroy(): void {
    this.routeSubscription?.unsubscribe();
    this.editBlogPostSubscription?.unsubscribe();
    this.getBlogPostSubscription?.unsubscribe();
    this.deleteBlogPostSubscription?.unsubscribe();
    this.imageSelectSubscription?.unsubscribe();
  }
}
<div class="container">
  <h1 class="mt-3">Edit BlogPost</h1>

  <ng-container *ngIf="model">
    <form #form="ngForm" (ngSubmit)="onFormSubmit()">
      <div class="mt-2">
        <div class="mt-3">
          <label class="form-lable" for="title">Title</label>
          <input
            class="form-control"
            type="text"
            id="title"
            name="title"
            [(ngModel)]="model.title"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="urlHandle">UrlHandle</label>
          <input
            class="form-control"
            type="text"
            id="urlHandle"
            name="urlHandle"
            [(ngModel)]="model.urlHandle"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="shortDescription"
            >Short Description</label
          >
          <input
            class="form-control"
            type="text"
            id="shortDescription"
            name="shortDescription"
            [(ngModel)]="model.shortDescription"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="content">Content</label>
          <div class="d-flex flex-row">
            <div class="col-6 p-2">
              <textarea
                class="form-control"
                style="height: 350px; max-height: 350px"
                name="content"
                id="content"
                [(ngModel)]="model.content"
              ></textarea>
            </div>
            <div
              class="col-6 p-2"
              style="max-height: 350px; overflow-y: scroll"
            >
              <markdown [data]="model.content"></markdown>
            </div>
          </div>
        </div>
        <div class="mt-3">
          <label class="form-lable" for="featuredImageUrl"
            >Featured Image Url
            <button
              class="btn btn-light"
              type="button"
              (click)="openImageSelector()"
            >
              Select Image
            </button>
          </label>
          <ng-container *ngIf="model.featuredImageUrl !== ''">
            <div>
              <img
                class="my-2"
                [src]="model.featuredImageUrl"
                alt=""
                height="100"
              />
            </div>
          </ng-container>
          <input
            class="form-control"
            type="text"
            id="featuredImageUrl"
            name="featuredImageUrl"
            [(ngModel)]="model.featuredImageUrl"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="publishedDate">Published Date</label>
          <input
            class="form-control"
            type="date"
            id="publishedDate"
            name="publishedDate"
            value="{{ model.publishedDate }}"
            [ngModel]="model.publishedDate | date : 'yyyy-MM-dd'"
            (ngModelChange)="model.publishedDate = $event"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="author">Author</label>
          <input
            class="form-control"
            type="text"
            id="author"
            name="author"
            [(ngModel)]="model.author"
          />
        </div>
        <div class="mt-3 form-check">
          <input
            class="form-check-input"
            type="checkbox"
            id="isVisible"
            name="isVisible"
            [(ngModel)]="model.isVisible"
          />
          <label class="form-check-label" for="isVisible"> Is Visible? </label>
        </div>
        <div class="mt-3">
          <label class="form-lable" for="categories">Categories</label>
          <ng-container *ngIf="categories$ | async as categories">
            <select
              class="form-control"
              name="categories"
              id="categories"
              [(ngModel)]="selectedCategories"
              multiple
            >
              <option *ngFor="let category of categories" [value]="category.id">
                {{ category.name }}
              </option>
            </select></ng-container
          >
        </div>

        <div class="mt-3 d-flex">
          <button class="btn btn-primary" type="submit">Save</button>
          <button
            class="btn btn-danger ms-3"
            type="button"
            (click)="onDelete()"
          >
            Delete
          </button>
        </div>
      </div>
    </form>
  </ng-container>
</div>

<!-- Image Selectore -->
<div
  class="images-container-model"
  [ngClass]="{
    'd-block': isImageSelectorVisible,
    'd-none': !isImageSelectorVisible
  }"
>
  <button
    class="btn btn-light"
    style="position: fixed; top: 10px; right: 10px"
    type="button"
    (click)="closeImageSelector()"
  >
    X
  </button>
  <app-image-selector></app-image-selector>
</div>
