import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Observable, Subscription } from 'rxjs';
import { BlogPostService } from '../services/blog-post.service';
import { BlogPost } from '../models/blog-post.model';
import { CategoryService } from '../../category/services/category.service';
import { Category } from '../../category/models/category.model';
import { EditBlogPost } from '../models/edit-blog-post-model';

@Component({
  selector: 'app-edit-blogpost',
  templateUrl: './edit-blogpost.component.html',
  styleUrls: ['./edit-blogpost.component.css'],
})
export class EditBlogpostComponent implements OnInit, OnDestroy {
  id: string | null = null;
  model?: BlogPost;
  categories$?: Observable<Category[]>;
  selectedCategories?: string[];
  routeSubscription?: Subscription;
  getBlogPostSubscription?: Subscription;
  editBlogPostUnSubscription?: Subscription;
  constructor(
    private route: ActivatedRoute,
    private blogPostService: BlogPostService,
    private categoryService: CategoryService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.categories$ = this.categoryService.getAllCategories();

    this.routeSubscription = this.route.paramMap.subscribe({
      next: (params) => {
        this.id = params.get('id');

        //Get BlogPost from API
        if (this.id) {
          this.getBlogPostSubscription = this.blogPostService
            .getBlogPostById(this.id)
            .subscribe({
              next: (res) => {
                this.model = res;
                this.selectedCategories = res.categories.map((x) => x.id);
              },
            });
        }
      },
    });
  }

  onFormSubmit(): void {
    // Convert this model to Request Object
    if (this.model && this.id) {
      var editBlogPost: EditBlogPost = {
        author: this.model.author,
        content: this.model.content,
        shortDescription: this.model.shortDescription,
        featuredImageUrl: this.model.featuredImageUrl,
        isVisible: this.model.isVisible,
        publishedDate: this.model.publishedDate,
        title: this.model.title,
        urlHandle: this.model.urlHandle,
        categories: this.selectedCategories ?? [],
      };

      this.editBlogPostUnSubscription = this.blogPostService
        .editBlogPost(this.id, editBlogPost)
        .subscribe({
          next: (res) => {
            this.router.navigateByUrl('/admin/blogpost');
          },
        });
    }
  }

  ngOnDestroy(): void {
    this.routeSubscription?.unsubscribe();
    this.getBlogPostSubscription?.unsubscribe();
    this.editBlogPostUnSubscription?.unsubscribe();
  }
}
<div class="container">
  <h1 class="mt-3">Edit BlogPost</h1>


  <ng-container *ngIf="model">
    <form #form="ngForm" (ngSubmit)="onFormSubmit()">
      <div class="mt-2 mb-5">
        <div class="mt-3">
          <label for="title" class="form-label">Title</label>
          <input type="text" id="title" name="title" class="form-control" [(ngModel)]="model.title">
        </div>

        <div class="mt-3">
          <label for="urlHandle" class="form-label">Url Handle</label>
          <input type="text" id="urlHandle" name="urlHandle" class="form-control" [(ngModel)]="model.urlHandle">
        </div>

        <div class="mt-3">
          <label for="shortDescription" class="form-label">Short Description</label>
          <input type="text" id="shortDescription" name="shortDescription" class="form-control"
            [(ngModel)]="model.shortDescription">
        </div>

        <div class="mt-3">
          <label for="content" class="form-label">Content</label>

          <div class="d-flex flex-row">
            <div class="col-6 p-2">
              <textarea id="content" name="content" class="form-control" style="height: 350px;max-height: 350px;"
                [(ngModel)]="model.content">
            </textarea>
            </div>

            <div class="col-6 p-2" style="max-height: 350px; overflow-y: scroll;">
              <markdown [data]="model.content"></markdown>
            </div>
          </div>

        </div>

        <div class="mt-3">
          <label for="featuredImageUrl" class="form-label">Featured Image Url
            <button type="button" class="btn btn-light" (click)="openImageSelector()">Select Image</button>
          </label>

          <ng-container *ngIf="model.featuredImageUrl !== ''">
            <div>
              <img [src]="model.featuredImageUrl" height="100" class="my-2">
            </div>
          </ng-container>

          <input type="text" id="featuredImageUrl" name="featuredImageUrl" class="form-control"
            [(ngModel)]="model.featuredImageUrl">
        </div>

        <div class="mt-3">
          <label for="publishedDate" class="form-label">Published Date</label>
          <input type="date" id="publishedDate" name="publishedDate" class="form-control"
            value="{{model.publishedDate}}" [ngModel]="model.publishedDate | date: 'yyy-MM-dd'"
            (ngModelChange)="model.publishedDate = $event">
        </div>

        <div class="mt-3">
          <label for="author" class="form-label">Author</label>
          <input type="text" id="author" name="author" class="form-control" [(ngModel)]="model.author">
        </div>

        <div class="form-check mt-3">
          <input class="form-check-input" type="checkbox" id="isVisible" [(ngModel)]="model.isVisible" name="isVisible">
          <label class="form-check-label" for="isVisible">
            Is Visible?
          </label>
        </div>

        <div class="mt-3">
          <label for="categories" class="form-label">Categories</label>

          <ng-container *ngIf="categories$ | async as categories">
            <select class="form-control" [(ngModel)]="selectedCategories" name="categories" id="categories" multiple>
              <option *ngFor="let category of categories" [value]="category.id">{{category.name}}</option>
            </select>
          </ng-container>

        </div>

        <div class="mt-3 d-flex">
          <button type="submit" class="btn btn-primary">Save</button>
        </div>
      </div>
    </form>
  </ng-container>
</div>
