import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { CategoryService } from '../services/category.service';
import { Category } from '../models/category.model';
import { EditCategoryRequest } from '../models/edit-category-request.model';

@Component({
  selector: 'app-edit-category',
  templateUrl: './edit-category.component.html',
  styleUrls: ['./edit-category.component.css'],
})
export class EditCategoryComponent implements OnInit, OnDestroy {
  id: string | null = null;
  paramsSubscription?: Subscription;
  editCategorySubscription?: Subscription;
  category?: Category;

  constructor(
    private route: ActivatedRoute,
    private categoryService: CategoryService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.paramsSubscription = this.route.paramMap.subscribe({
      next: (params) => {
        this.id = params.get('id');

        if (this.id) {
          //get data from api for category id
          this.categoryService.getCategoryById(this.id).subscribe({
            next: (res) => {
              this.category = res;
            },
          });
        }
      },
    });
  }

  onFormSubmit(): void {
    const editCategoryRequest: EditCategoryRequest = {
      name: this.category?.name ?? '',
      urlHandle: this.category?.urlHandle ?? '',
    };

    //pass to service
    if (this.id) {
      this.editCategorySubscription = this.categoryService
        .editCategory(this.id, editCategoryRequest)
        .subscribe({
          next: (res) => {
            this.router.navigateByUrl('/admin/categories');
          },
        });
    }
  }

  ngOnDestroy(): void {
    this.paramsSubscription?.unsubscribe();
    this.editCategorySubscription?.unsubscribe();
  }
}
<div class="container">
  <h1 class="mt-3">Edit Category</h1>
  <!-- Condition -->
  <ng-container *ngIf="category; else notFound">
    <!-- Form -->
    <form #form="ngForm" (ngSubmit)="onFormSubmit()">
      <div class="mt-2">
        <div class="mt-3">
          <label class="form-lable" for="categoryId">Category ID</label>
          <input
            class="form-control"
            type="text"
            id="categoryId"
            name="categoryId"
            value="{{ category.id }}"
            disabled
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="categoryId">Category Name</label>
          <input
            class="form-control"
            type="text"
            id="categoryName"
            name="categoryName"
            [(ngModel)]="category.name"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="categoryId">Category UrlHandle</label>
          <input
            class="form-control"
            type="text"
            id="categoryUrlHandle"
            name="categoryUrlHandle"
            [(ngModel)]="category.urlHandle"
          />
        </div>

        <div class="d-flex mt-3">
          <button class="btn btn-primary" type="submit">Save</button>
          <button class="btn btn-danger" type="submit">Save</button>

        </div>
      </div>
    </form>
  </ng-container>

  <ng-template #notFound>
    <div class="alert alert-warning" role="alert">Category Not Found!</div>
  </ng-template>
</div>
