import { Component, OnInit } from '@angular/core';
import { BlogPostService } from '../../blog-post/services/blog-post.service';
import { Observable } from 'rxjs';
import { BlogPost } from '../../blog-post/models/blog-post.model';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {
  blogs$?: Observable<BlogPost[]>;

  constructor(private blogPostService: BlogPostService) {}
  ngOnInit(): void {
    this.blogs$ = this.blogPostService.getAllBlogPosts();
  }
}
<div class="container">
  <div class="my-5">
    <div class="row align-items-strech">
      <ng-container *ngIf="blogs$ | async as blogs">
        <div class="col-12 col-md-4" *ngFor="let blog of blogs">
          <div class="card h-100">
            <img
              class="card-img-top"
              [src]="blog.featuredImageUrl"
              [alt]="blog.title"
            />
            <div class="card-body d-felx felx-column">
              <h5 class="card-title">
                {{ blog.title }}
              </h5>
              <p class="card-text">{{ blog.shortDescription }}</p>
              <span class="d-flex align-items-end" style=""></span>
            </div>
          </div>
        </div>
      </ng-container>
    </div>
  </div>
</div>
