import { Component } from '@angular/core';
import { LoginRequest } from '../models/login-request.model';
import { AuthService } from '../services/auth.service';
import { CookieService } from 'ngx-cookie-service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  model: LoginRequest;

  constructor(
    private authService: AuthService,
    private cookieService: CookieService
  ) {
    this.model = {
      email: '',
      password: '',
    };
  }

  onSubmitForm(): void {
    this.authService.login(this.model).subscribe({
      next: (res) => {
        //Set Auth Cookie
        this.cookieService.set('Authorization', `Bearer ${res.token}`, undefined, '/', undefined,true,'');
      },
    });
  }
}
<div class="container">
  <div class="mt-5">
    <div class="col-12 col-mb-6 col-lg-4 mx-auto">
      <h1>Login</h1>

      <form #form="ngForm" (ngSubmit)="onSubmitForm()">
        <div class="mt-3">
          <label class="form-lable" for="email">Email</label>
          <input
            type="text"
            class="form-control"
            id="email"
            name="email"
            [(ngModel)]="model.email"
          />
        </div>
        <div class="mt-3">
          <label class="form-lable" for="password">Password</label>
          <input
            type="password"
            class="form-control"
            id="password"
            name="password"
            [(ngModel)]="model.password"
          />
        </div>

        <div class="mt-3">
          <button type="submit" class="btn btn-primary">Login</button>
        </div>
      </form>
    </div>
  </div>
</div>
