import { Component, OnInit } from '@angular/core';
import { ImageService } from './image.service';
import { Observable } from 'rxjs';
import { BlogImage } from '../../models/blog-image-model';

@Component({
  selector: 'app-image-selector',
  templateUrl: './image-selector.component.html',
  styleUrls: ['./image-selector.component.css'],
})
export class ImageSelectorComponent implements OnInit {
  private file?: File;
  fileName: string = '';
  title: string = '';
  images$?: Observable<BlogImage[]>;

  constructor(private imageService: ImageService) {}
  ngOnInit(): void {
    this.getImages();
  }

  /* Methods */
  onFileUploadChange(event: Event): void {
    const element = event.currentTarget as HTMLInputElement;
    this.file = element.files?.[0];
  }
  uploadImage(): void {
    if (this.file && this.fileName !== '' && this.title !== '') {
      this.imageService
        .uploadImage(this.file, this.fileName, this.title)
        .subscribe({
          next: (res) => {
            this.getImages();
          },
        });
    }
  }

  private getImages() {
    this.images$ = this.imageService.getAllImages();
  }
}
<div class="card mt-5">
  <div class="card-header">
    <h3 class="card-title">Images</h3>
  </div>
  <div class="card-body">
    <div class="d-flex flex-md-rowvflex-sm-column flex-wrap">
      <div class="col-8 p-4" style="overflow-y: scroll;">
      <ng-container *ngIf="images$ | async as images"></ng-container></div>
      <div class="col-4 p-4 bg-light">
        <h5>Upload Image</h5>

        <form #form="ngForm" (ngSubmit)="uploadImage()">
          <div class="mt-3">
            <input
              class="form-control"
              type="file"
              (change)="onFileUploadChange($event)"
            />
          </div>
          <div class="mt-3">
            <input
              class="form-control"
              type="text"
              placeholder="File Name"
              name="filename"
              [(ngModel)]="fileName"
            />
          </div>
          <div class="mt-3">
            <input
              class="form-control"
              type="text"
              placeholder="Title"
              name="title"
              [(ngModel)]="title"
            />
          </div>
          <div class="mt-3">
            <button class="btn btn-dark" type="submit">Upload</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
